//Method that moves a piece to a new position
function move(posOld, posNew)
{
    let piece = find("position", posOld);
    if(piece !== null)
    {
        piece.position = posNew;
        return {color: piece.color, type: piece.type, position: piece.position, posOld: posOld};
    }

    return null;
}

//--Miscellaneous
//Find first object in array by key
function find(key, value)
{
    for(let ii = 0; ii < board.length; ii++)
    {
        if(board[ii][key] == value)
        {
            return board[ii];
        }
    }

    return null;
}

module.exports = {
    board: newBoard,
    move: move};