var game = new Object(
    id = 0,
    pOne = 0,
    pTwo = 0,
    options = {},
    status = 0
)

function Game(id, pOne, pTwo, options, status){
    this.id = id;
    this.pOne = pOne;
    this.pTwo = pTwo;
    this.options = options;
    this.status = status
}

function newGame(id, pOne, pTwo, options, status)
{
    return new Game(id, pOne, pTwo, options, status);
}

module.exports = {game: game, newGame: newGame};