var game = new Object(
    id = 0,
    players = [],
    board = [],
    options = {},
    status = 0
)

function Game(id, players, board, options, status){
    this.id = id;
    this.players = players;
    this.board = board;
    this.options = options;
    this.status = status
}

function newGame(id, players, board, options, status)
{
    return new Game(id, players, board, options, status);
}

module.exports = {game: game, newGame: newGame};