//Validate move

//Make move
function move(board, posOld, posNew)
{
    let piece = find(board, "position", posOld);
    if(piece !== null)
    {
        piece.position = posNew;
        return {color: piece.color, type: piece.type, position: piece.position, posOld: posOld};
    }

    return null;
}

//--Miscellaneous
//Find first object in array by key
function find(array, key, value)
{
    for(let ii = 0; ii < array.length; ii++)
    {
        if(array[ii][key] == value)
        {
            return array[ii];
        }
    }

    return null;
}

module.exports = {move: move};